function plot_2(x,y1,y2)    % plot var_x with var_y1  plot(x, y1);  hold on  plot(x, y2);  hold off    % adding legend  legend('p6 function', 'p7 function');    % enable grid  grid on;    % hold the above plot or figure  hold on;      % adding label to the x-axis  xlabel('year');      % adding lable to the y-axis  ylabel('result of interpolation');      % adding title for the plot  title('Number of Prediction Populations in million');  endfunction